targets.queryText:
query {
 data:storageBuckets {
    acceptingNewBags
    id
    operatorMetadata {
      nodeLocation {
        coordinates {
          latitude
          longitude
        }
        city
        countryCode
      }
}}}

query {
data:workers(
    where: { group: { id_eq: "storageWorkingGroup" }, isActive_eq: true }) {
   workerId:runtimeId
   membership {
   id
   handle
   }
   rewardPerBlock
   rewardAccount
   }
   }

query {
  data:storageBuckets {
    id
    operatorStatus {
      ... on StorageBucketOperatorStatusActive {
        workerId
      }
    }
    acceptingNewBags
    dataObjectCountLimit
    dataObjectsSizeLimit
    dataObjectsCount
    dataObjectsSize
    operatorMetadata {
      nodeEndpoint
      nodeLocation {
        city
        countryCode
      }
    }

  }

}
